import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { createArticle, updateArticle, deleteArticle, getArticle } from '../api';

const ArticlesList = ({ publishedFilter }) => {
  const [articles, setArticles] = useState([]);

  const fetchArticles = useCallback(async () => {
    const response = await getArticle(publishedFilter);
    setArticles(response.data);
  }, [publishedFilter]);

  useEffect(() => {
    fetchArticles();
  }, [fetchArticles, publishedFilter]);

  return (
    <div>
      <h2>Articles List</h2>
      {articles.map((article) => (
        <div key={article.id}>
          <h3>{article.title}</h3>
          <p>{article.content}</p>
        </div>
      ))}
    </div>
  );
};

export default ArticlesList;
