import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { createArticle, updateArticle, deleteArticle, getArticle } from '../api';

const ArticlesList = () => {
  const [articles, setArticles] = useState([]);
  const [publishedFilter, setPublishedFilter] = useState(null);

  const fetchArticles = () => {
    getArticle(publishedFilter)
      .then((response) => setArticles(response.data))
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    fetchArticles();
  }, [publishedFilter]);

  const handlePublishedFilterChange = (event) => {
    const value = event.target.value === 'true' ? true : event.target.value === 'false' ? false : null;
    setPublishedFilter(value);
  };

  const filteredArticles = publishedFilter === null ? articles : articles.filter((article) => article.published === publishedFilter);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this article?')) {
      deleteArticle(id)
        .then(() => fetchArticles())
        .catch((error) => console.log(error));
    }
  };

  return (
    <div>
      <h1>Articles</h1>
      <div>
        <label>Filter by published status:</label>
        <select value={publishedFilter} onChange={handlePublishedFilterChange}>
          <option value={null}>All</option>
          <option value={true}>Published</option>
          <option value={false}>Unpublished</option>
        </select>
      </div>
      <Link to="/articles/new">Add New Article</Link>
      <ul>
        {filteredArticles.map((article) => (
          <li key={article.id}>
            <Link to={`/articles/${article.id}`}>{article.title}</Link>
            <button onClick={() => handleDelete(article.id)}>Delete</button>
            <Link to={`/articles/${article.id}/edit`}>Edit</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ArticlesList;
