import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

function EditArticle(props) {
  const { id } = useParams();
  const history = useNavigate();
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [published, setPublished] = useState(false);

  useEffect(() => {
    async function fetchArticle() {
      try {
        const response = await axios.get(`http://localhost:3000/articles/${id}`);
        setTitle(response.data.title);
        setBody(response.data.body);
        setPublished(response.data.published);
      } catch (error) {
        console.error(error);
      }
    }

    fetchArticle();
  }, [id]);

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleBodyChange = (event) => {
    setBody(event.target.value);
  };

  const handlePublishedChange = (event) => {
    setPublished(event.target.checked);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const article = { title, body, published };
    try {
      await axios.put(`http://localhost:3000/articles/${id}`, article);
      history.push(`/articles/${id}`);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Edit Article</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input type="text" id="title" value={title} onChange={handleTitleChange} />
        </div>
        <div>
          <label htmlFor="body">Body:</label>
          <textarea id="body" value={body} onChange={handleBodyChange} />
        </div>
        <div>
          <label htmlFor="published">Published:</label>
          <input type="checkbox" id="published" checked={published} onChange={handlePublishedChange} />
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default EditArticle;
