import { useEffect, useState } from 'react';
import { getArticle, updateArticle } from '../api';

const EditArticle = ({ match }) => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [published, setPublished] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchArticle = async () => {
      const articleData = await getArticle(match.params.id);
      setTitle(articleData.title);
      setBody(articleData.body);
      setPublished(articleData.published);
      setLoading(false);
    };
    fetchArticle();
  }, [match.params.id]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const article = { title, body, published };
    updateArticle(match.params.id, article)
      .catch((error) => console.log(error));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Edit Article</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input type="text" id="title" value={title} onChange={(event) => setTitle(event.target.value)} />
        </div>
        <div>
          <label htmlFor="body">Body:</label>
          <textarea id="body" value={body} onChange={(event) => setBody(event.target.value)} />
        </div>
        <div>
          <label htmlFor="published">Published:</label>
          <input type="checkbox" id="published" checked={published} onChange={(event) => setPublished(event.target.checked)} />
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default EditArticle;
