import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { getArticles, updateArticle, deleteArticle } from '../api';

const ArticlesList = ({ publishedFilter }) => {
  const [articles, setArticles] = useState([]);

  const fetchArticles = useCallback(async () => {
    const response = await getArticles(publishedFilter);
    setArticles(response.data);
  }, [publishedFilter]);

  useEffect(() => {
    fetchArticles();
  }, [fetchArticles, publishedFilter]);

  const handleDelete = async (id) => {
    await deleteArticle(id);
    fetchArticles();
  };

  const handleTogglePublish = async (id, published) => {
    await updateArticle(id, { published: !published });
    fetchArticles();
  };

  return (
    <div>
      <h2>Articles List</h2>
      <div>
        <label htmlFor="publishedFilter">Filter by Published/Unpublished:</label>
        <select id="publishedFilter" value={publishedFilter} onChange={(event) => window.location.href = `/articles?published=${event.target.value}`}>
          <option value="">All</option>
          <option value="true">Published</option>
          <option value="false">Unpublished</option>
        </select>
      </div>
      <Link to="/articles/new">Create Article</Link>
      {articles.map((article) => (
        <div key={article.id}>
          <h3>{article.title}</h3>
          <p>{article.body}</p>
          <p>{article.published ? 'Published' : 'Unpublished'}</p>
          <div>
            <button onClick={() => handleTogglePublish(article.id, article.published)}>{article.published ? 'Unpublish' : 'Publish'}</button>
            <Link to={`/articles/${article.id}`}>View Details</Link>
            <Link to={`/articles/${article.id}/edit`}>Edit</Link>
            <button onClick={() => handleDelete(article.id)}>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ArticlesList;
