import { useState, useEffect } from "react";
import { useParams} from "react-router-dom";
import { useHistory } from "react-router";
import axios from "axios";

function EditArticle() {
  const { id } = useParams();
  const history = useHistory();
  const [article, setArticle] = useState({});

  useEffect(() => {
    async function fetchArticle() {
      const response = await axios.get(`http://localhost:3000/articles/${id}`);
      setArticle(response.data);
    }

    fetchArticle();
  }, [id]);

  const [title, setTitle] = useState(article.title || "");
  const [body, setBody] = useState(article.body || "");
  const [published, setPublished] = useState(article.published || false);

  const handleSubmit = async (event) => {
    event.preventDefault();

    const updatedArticle = {
      title,
      body,
      published,
    };

    await axios.put(`http://localhost:3000/articles/${id}`, updatedArticle);

    history.push(`/articles/${id}`);
  };

  return (
    <div>
      <h1>Edit Article</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input type="text" id="title" value={title} onChange={(event) => setTitle(event.target.value)} />
        </div>
        <div>
          <label htmlFor="body">Body:</label>
          <textarea id="body" value={body} onChange={(event) => setBody(event.target.value)} />
        </div>
        <div>
          <label htmlFor="published">Published:</label>
          <input type="checkbox" id="published" checked={published} onChange={(event) => setPublished(event.target.checked)} />
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default EditArticle;
